        try{
            const services = await service.findAll();
            return services;
        }catch(error){
            console.log('Error : ',error);
        }
    },
    async deleteService(req){
        try{
            const {
                params:{
                    categoryId,
                    serviceId
                }
            } = req;
            const isServiceDeleted = await service.update({categoryId:null},{where:{id:serviceId}});
            return true;
        }catch(error){
            console.log('Error : ',error);
        }
    }
}

